import React, { useState } from 'react'

const [todoVal, setTodoVal] = useState('')
const [todos, setTodos] = useState([])
const TodoList=()=> {
    const handleChange = e => {
        setTodoVal(e.target.value)
    }
    const handleSubmit = e => {
        e.preventDefault()
        const todo = {
            value: todoVal,
            done: false
        }
        if (!todoVal) return;
        setTodos([...todos, todo]);
        document.getElementById('todoVal').value = ''
    }
    const deleteItem = e => {
        const { id } = e.target.parentElement;
        todos.splice(id, 1)
        setTodos([...todos]);
    }
    const handleDone = e => {
        const { id } = e.target.parentElement;
        todos[id].done = !todos[id].done
        setTodos([...todos])
    }
    return (
        <div>
            <h1 className='heading'>ToDo-List</h1>
            <header className='head'>
                <label className='lab'>Add-items:</label>
                <form className='form' onSubmit={handleSubmit}>
                    <input type='text' onChange={handleChange}
                        className='input-text' />
                    <button className="add-btn">+</button>
                </form>
            </header>
            <div className='todos'>
                {
                    todos && todos.map((todo, i) => (
                        <div className='container' key={todo.value} id={i}>
                            <li className={todo.done ? 'done' : 'not done'} onClick={handleDone}>
                                {todo.value} </li>
                            <button className='Delete-btn' onClick={deleteItem}> Del </button>
                        </div>
                    ))
                }

            </div>
        </div>
    )
}

export default TodoList
